<style>.input-group-addon{font-weight:bold}input:invalid{border:1px solid blue}input:valid{border:1px solid green}</style>
  <div class="input-group"><span class="input-group-addon" id="sizing-addon2">Cédula</span><input type="text" class="form-control"  aria-describedby="sizing-addon2" id="cedula_representante" name="cedula_representante" minlength="10" maxlength="13" onkeyup = "if(event.keyCode == 13) validarcedularepresentante()" required></div>
  <div class="input-group"><span class="input-group-addon" id="sizing-addon2">Nombre</span><input type="text" class="form-control"  aria-describedby="sizing-addon2" id="nombre_representante" name="nombre_representante"  maxlength="50" required ></div>
<div class="input-group">
